let form=document.querySelector(".subscribe-form"),email=document.querySelector(".email"),mes=document.querySelector(".error-message");function validateForm(){form.addEventListener("submit",(function(e){e.preventDefault(),email.value?validateEmail(email.value)?email.classList.remove("error"):(mes.innerHTML="Please provide a valid email",email.classList.add("error")):(mes.innerHTML="Whoops! It looks like you forgot to add your email",email.classList.add("error"))}))}function validateEmail(e){return/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(String(e).toLowerCase())}validateForm(),console.log(validateEmail("grigorieva.ms@yandex.ru"));